const buttonThatOpensTheMap=document.querySelector(".map-link"),mapPopup=document.querySelector(".map-popup"),closeMapPopup=document.querySelector(".map-popup-close-btn");buttonThatOpensTheMap.addEventListener("click",function(p){p.preventDefault(),mapPopup.classList.add("map-popup-opened")}),closeMapPopup.addEventListener("click",function(p){p.preventDefault(),mapPopup.classList.remove("map-popup-opened")}),window.addEventListener("keydown",function(p){27===p.keyCode&&mapPopup.classList.contains("map-popup-opened")&&(p.preventDefault(),mapPopup.classList.remove("map-popup-opened"))});
